type AdditionalInfo {
  made: String
  disign: String
  warranty: String
  AnnualFailureRate: Float
}

type Customer {
  id: String
  name: String
  email: String
  age: Int
  status: Boolean
  products: [Product]
  data: ExtraInfo
}

"""Date custom scalar type"""
