type ExtraInfo {
  LicenseType: String
  Status: String
}

type Mutation {
  addCustomer(name: String!, email: String!, age: Int!): Customer
  editCustomer(id: String!, name: String, email: String, age: Int, extraData: Date): Customer
}

type Product {
  productID: Int
  productName: String
  quantity: Int
